	/**
	 * 以上代码依据 MySQL 源代码中对于 GB2312 / GBK / Big5 这几种常见中文编码格式的双字节范围判断改写。你还可以从 MySQL 源码包的 strings/ctype-*.c
	 * 系列文件中找到其他语言字符编码的范围定义<br />
	 * http://www.zeali.net/entry/444
	 * 
	 * @param ch
	 * @return
	 */

	/* Support for Chinese(GB2312) characters */
	// #define isgb2312head(c) (0xa1<=(uchar)(c) && (uchar)(c)<=0xf7)
	// #define isgb2312tail(c) (0xa1<=(uchar)(c) && (uchar)(c)<=0xfe)
	public static boolean isGB2312(char ch) {
		byte head;
		byte tail;
		byte[] bytes = getBytes(ch);

		if (bytes.length == 1)
			return false;
		head = bytes[0];
		tail = bytes[1];

		int iHead = head & 0xff;
		int iTail = tail & 0xff;
		return ((iHead >= 0xa1 && iHead <= 0xf7 && iTail >= 0xa1 && iTail <= 0xfe) ? true : false);
	}

	/* Support for Chinese(GBK) characters */
	// #define isgbkhead(c) (0x81<=(uchar)(c) && (uchar)(c)<=0xfe)
	// #define isgbktail(c) ((0x40<=(uchar)(c) && (uchar)(c)<=0x7e)
	// || (0x80<=(uchar)(c) && (uchar)(c)<=0xfe))
	public static boolean isGBK(char ch) {
		byte head;
		byte tail;
		byte[] bytes = getBytes(ch);

		if (bytes.length == 1)
			return false;
		head = bytes[0];
		tail = bytes[1];

		int iHead = head & 0xff;
		int iTail = tail & 0xff;
		return ((iHead >= 0x81 && iHead <= 0xfe && (iTail >= 0x40 && iTail <= 0x7e || iTail >= 0x80 && iTail <= 0xfe)) ? true
				: false);
	}

	/* Support for Chinese(BIG5) characters */
	// #define isbig5head(c) (0xa1<=(uchar)(c) && (uchar)(c)<=0xf9)
	// #define isbig5tail(c) ((0x40<=(uchar)(c) && (uchar)(c)<=0x7e)
	// || (0xa1<=(uchar)(c) && (uchar)(c)<=0xfe))
	public static boolean isBIG5(char ch) {
		byte head;
		byte tail;
		byte[] bytes = getBytes(ch);

		if (bytes.length == 1)
			return false;
		head = bytes[0];
		tail = bytes[1];

		int iHead = head & 0xff;
		int iTail = tail & 0xff;
		return ((iHead >= 0xa1 && iHead <= 0xf9 && (iTail >= 0x40 && iTail <= 0x7e || iTail >= 0xa1 && iTail <= 0xfe)) ? true
				: false);
	}

	private static byte[] getBytes(char ch) {
		return String.valueOf(ch).getBytes();
	}

