	/**
	 * 得到这段文本里非常用的汉字的个数
	 * 
	 * @param v
	 * @param option 0-最严格的，匹配生僻字、难检字；10-常见的3000字；20-常见的8000字
	 * @return
	 */
	public static int getUnfrequentCharCount(String v, int option) {
		if (Validate.isNullOrEmpty(v))
			return 0;

		int count = 0;
		int len = v.length();
		char ch;

		for (int i = 0; i < len; i++) {
			ch = v.charAt(i);
			if (!Validate.isChineseChar(ch)) // 排除非汉字的字符
				continue;

			if (option == 0) { // 最严格的，匹配生僻字
				if (setEx != null && setEx.contains(ch)) {
					count++;
				}
			} else if (option == 10) {
				if (!frequencyMap.containsKey(ch))
					count++;
			} else {
				if (!isCommonChar(ch)) {
					count++;
				}
			}
		}

		return count;
	}

